mapper.update:
update productreviews

set breview = breview + 1

where bno = #{bno}

update productreviews

set bno = bno + 1

where bno = #{bno}

mapper.select:
select count(*) as cnt from productreviews 

where bwriter like #{keyword}


where bcontent like #{keyword}

select bno, btitle, bwriter, bcontent, bimg, breview, bregdate
from productreviews 
where bno = #{bno}
